function process() {
  const TBP = GmailApp.getUserLabelByName("TBP");
  const threads = TBP.getThreads();
  for (let a = 0; a < threads.length; a++) {
    //Get messages in thread
    const messages = threads[a].getMessages();
    //Get last message
    const message = messages[messages.length - 1];
    try {
      if (message.getFrom().split("<").length > 1) {
        if (message.getFrom().split("<")[1].split(">")[0] ===
          "no-reply@classroom.google.com") {
          GmailApp.getUserLabelByName("classmate").addToThread(threads[a]);
          if (message.getSubject().split(":")[0] === "Graded") {
            GmailApp.getUserLabelByName("grade report").addToThread(threads[a]);
          }
        }
      }
      if (message.getFrom().split("@")[1].split(">")[0] === "k12.school.edu") {
        const body = message.getPlainBody().toLowerCase();
        GmailApp.getUserLabelByName("ai").addToThread(threads[a]);
        //Check if we need the bot to reply
        if (
          body.includes("bee") ||
          body.includes("movie") ||
          body.includes("script")
        ) {
          sendBeeScript(message);
        }
        if (body.includes("hello")) {
          sendHello(message)
        }
      } else if (message.getFrom().split("@")[1].split(">")[0] === "school.edu") {
        GmailApp.getUserLabelByName("teacher").addToThread(threads[a]);
      } else {
        let isTrash = false
        if (message.getFrom().split("<").length > 1) {
          if (message.getFrom().split("<")[1].split(">")[0] ==="no-reply@classroom.google.com") {
            isTrash = true
          }
        }
        if (!isTrash){
        GmailApp.getUserLabelByName("other").addToThread(threads[a]);
        }
      }
      //Remove from TBP
      threads[a].removeLabel(TBP);
    } catch (e) {}
  }
}

function sendHello(message) {
  //var img = DriveApp.getFileById('17gg94LX2xX2ly2E4M1VBKeLU458Ir9ln')
  message.reply("", {
    name: "Hello.",
    htmlBody: "<h1 style='font-size=2em;'>Hello there.</h1>",
  });
}

